mapper.select:
SELECT A.*, B.* FROM ( SELECT ff.rnum "num", ff.food_sq "foodSq", ff.food_name "foodName", ff.food_kcal "foodKcal", ff.food_ct "foodCt" FROM ( SELECT ROWNUM rnum, f.* FROM ( SELECT food_sq, food_name, food_kcal, food_ct FROM food_t
ORDER BY food_name ASC ) f ) ff WHERE rnum BETWEEN (#{pageSize}*#{pageNum}-1)+1 AND (#{pageSize}*#{pageNum}-1)+#{pageSize} ) A CROSS JOIN ( SELECT COUNT(*) "totalNum" FROM food_t
) B

mapper.update:
UPDATE food_t 
SET food_name = #{foodName}, 
    food_kcal = #{foodKcal}, 
    food_ct = #{foodCt} 
WHERE food_sq = #{foodSq}

mapper.insert:
INSERT INTO food_t (
    food_name, 
    food_kcal, 
    food_ct
) VALUES (
    #{foodName}, 
    #{foodKcal}, 
    #{foodCt}
)
