mapper.update:
UPDATE work_t
SET work_read_cnt = NVL(work_read_cnt,0)+1 
WHERE work_sq = #{workSq}

mapper.select:
SELECT A.*, B.* FROM( SELECT tt1.rnum AS NUM, tt1.work_sq AS workSq, tt1.work_title AS workTitle, tt1.work_content AS workContent, tt1.work_read_cnt AS workReadCent, tt1.work_ct1 AS workCt1, DECODE(TO_CHAR(SYSDATE,'YYYYMMDD') ,TO_CHAR(tt1.work_reg_dt,'YYYY/MM/DD') ,TO_CHAR(tt1.work_reg_dt,'HH24:MI') ,TO_CHAR(tt1.work_reg_dt,'YYYY/MM/DD')) AS workRegDt FROM ( SELECT ROWNUM rnum, t1.* FROM ( SELECT * FROM work_t
ORDER BY work_reg_dt desc )t1 )tt1 WHERE rnum BETWEEN (#{pageSize} *(#{pageNum}-1)+1) AND #{pageSize}* (#{pageNum}-1) + #{pageSize} )A CROSS JOIN ( select COUNT(*) totalNum FROM work_t
)B
